contracts/BigBang_LendingService.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

contract BigBang_LendingService is AccessControl {
    using SafeMath for uint;

    address public owner;
    uint8 private tokensTotalCount;
    uint private usersTotalCount;
    uint private assetsTotalValue;
    uint private usedBigBangsTotalAmount;

    bytes32 private constant ACCESS_MANAGER_ROLE =
        keccak256("ACCESS_MANAGER_ROLE");
    bytes32 private constant DATA_MANAGER_ROLE = keccak256("DATA_MANAGER_ROLE");
    bytes32 private constant AUTHORIZED_CONTRACT_ROLE =
        keccak256("AUTHORIZED_CONTRACT_ROLE");
    //  bytes32 private constant ACTIVE_USER_ROLE = keccak256("ACTIVE_USER_ROLE");

    IERC20 private nativeTokenProvider;
    BusinessLogicDatas private businessLogicDatas;

    struct Token {
        address tokenContractAddr;
        IERC20 tokenProvider;
        AggregatorV3Interface priceFeed;
    }

    struct Loan {
        uint collateralAmount;
        uint borrowedAmount;
        uint expirationDate;
    }

    struct BusinessLogicDatas {
        address networkCoinPriceFeed;
        uint8 ownerFeePercent;
        uint voteFee;
        uint8 lendingLimitationPercent;
        uint lowestPrice;
        uint highestPrice; //10000044444222
        uint8 repaymentPeriod;
        uint ownerShare;
    }

    mapping(address => mapping(address => Loan)) private loans;
    mapping(address => Token) private tokens;

    event Borrowed(address indexed from, address indexed to, uint tokens);
    event Repaid(address indexed _from, address indexed _to, uint tokens);

    constructor(
        address _networkCoinPriceFeed,
        uint8 _ownerFeePercent,
        uint _voteFee,
        uint8 _lendingLimitationPercent,
        uint _lowestPrice,
        uint _highestPrice,
        uint8 _repaymentPeriod,
        uint _ownerShare
    ) {
        owner = msg.sender;

        _grantRole(DEFAULT_ADMIN_ROLE, owner);
        _grantRole(ACCESS_MANAGER_ROLE, owner);
        _grantRole(DATA_MANAGER_ROLE, owner);

        setBusinessLogicDatas(
            _networkCoinPriceFeed,
            _ownerFeePercent,
            _voteFee,
            _lendingLimitationPercent,
            _lowestPrice,
            _highestPrice,
            _repaymentPeriod,
            _ownerShare
        );
    }

    modifier onlyOwner(address _userAddr) {
        require(owner == _userAddr, "You are not owner of contract !");
        _;
    }

    //  modifier isUserActive(address _userAddr) {
    //      require(
    //          hasRole(ACTIVE_USER_ROLE, _userAddr),
    //          "This address is not an active user !"
    //      );
    //      _;
    //  }

    modifier isTokenSupported(address _tokenAddr) {
        require(
            _tokenAddr == address(this) ||
                _tokenAddr == tokens[_tokenAddr].tokenContractAddr ||
                _tokenAddr == address(nativeTokenProvider),
            "This token does not supported !"
        );
        _;
    }

    modifier isLoanExist(address _borrowerAddr, address _tokenAddr) {
        Loan memory loan = loans[_borrowerAddr][_tokenAddr];
        require(
            loan.borrowedAmount != 0 || loan.collateralAmount != 0,
            "This loan does not exist !"
        );
        _;
    }

    // function grantRole(string memory _role , address _account)
    //    public
    //    onlyRole(ACCESS_MANAGER_ROLE)
    // {
    //    bytes32 roleHash = keccak256(bytes(_role));

    //    require(!hasRole(roleHash , _account) , "Role already assigned to the account !");
    //    _grantRole(roleHash , _account);
    // }

    // function revokeRole(string memory _role , address _account)
    //    public
    //    onlyRole(ACCESS_MANAGER_ROLE)
    // {
    //    bytes32 roleHash = keccak256(bytes(_role));

    //    require(hasRole(roleHash , _account) , "Role not assigned to the account !");
    //    _revokeRole(roleHash , _account);
    // }

    // review this func
    //  function userActivation(address _userAddr) public {
    //      if (!hasRole(ACTIVE_USER_ROLE, _userAddr)) {
    //          grantRole(ACTIVE_USER_ROLE, _userAddr);
    //          usersTotalCount++;
    //      }
    //  }

    function getTokenLatestPrice(
        address _tokenAddr
    ) public view returns (uint) {
        require(
            _tokenAddr != address(nativeTokenProvider),
            "Wrong token address !"
        );

        if (_tokenAddr == address(this)) {
            (, int price, , , ) = AggregatorV3Interface(
                businessLogicDatas.networkCoinPriceFeed
            ).latestRoundData();
            return uint(price).mul(1e10);
        }

        (, int tokenPrice, , , ) = tokens[_tokenAddr]
            .priceFeed
            .latestRoundData();
        return uint(tokenPrice).mul(1e10);
    }

    function getUserBalance(
        address _tokenAddr,
        address _userAddr
    ) public view isTokenSupported(_tokenAddr) returns (uint) {
        if (_tokenAddr == address(this)) {
            return address(_userAddr).balance;
        } else if (_tokenAddr == address(nativeTokenProvider)) {
            return nativeTokenProvider.balanceOf(_userAddr);
        } else {
            return tokens[_tokenAddr].tokenProvider.balanceOf(_userAddr);
        }
    }

    function getBorrowerCollateralBalance(
        address _userAddr,
        address _tokenAddr
    )
        public
        view
        //   isUserActive(_userAddr)
        isTokenSupported(_tokenAddr)
        returns (uint)
    {
        return loans[_userAddr][_tokenAddr].collateralAmount;
    }

    function getBorrowerBorrowedAmount(
        address _userAddr,
        address _tokenAddr
    )
        public
        view
        //   isUserActive(_userAddr)
        isTokenSupported(_tokenAddr)
        returns (uint)
    {
        return loans[_userAddr][_tokenAddr].borrowedAmount;
    }

    function getLoanExpirationDate(
        address _borrowerAddr,
        address _tokenAddr
    )
        public
        view
        //   isUserActive(_borrowerAddr)
        isTokenSupported(_tokenAddr)
        returns (uint)
    {
        return loans[_borrowerAddr][_tokenAddr].expirationDate;
    }

    function getContractBalance(
        address _tokenAddr
    ) public view isTokenSupported(_tokenAddr) returns (uint) {
        if (_tokenAddr == address(this)) {
            return address(address(this)).balance;
        } else if (_tokenAddr == address(nativeTokenProvider)) {
            return nativeTokenProvider.balanceOf(address(this));
        } else {
            return tokens[_tokenAddr].tokenProvider.balanceOf(address(this));
        }
    }

    function getUsersTotalCount() public view returns (uint) {
        return usersTotalCount;
    }

    function getVoteFee() public view returns (uint) {
        return businessLogicDatas.voteFee;
    }

    function getRepaymentPeriod() public view returns (uint8) {
        return businessLogicDatas.repaymentPeriod;
    }

    function getLendingLimitation() public view returns (uint8) {
        return businessLogicDatas.lendingLimitationPercent;
    }

    //is just for test
    function getOwnerFeePercent() public view returns (uint8) {
        return businessLogicDatas.ownerFeePercent;
    }

    //is just for test
    function getHighestPrice() public view returns (uint) {
        return businessLogicDatas.highestPrice;
    }

    //is just for test
    function getLowestPrice() public view returns (uint) {
        return businessLogicDatas.lowestPrice;
    }

    //is just for test
    function getNetworkCoinPriceFeed() public view returns (address) {
        return businessLogicDatas.networkCoinPriceFeed;
    }

    function getOwnerShare() public view onlyOwner(msg.sender) returns (uint) {
        return businessLogicDatas.ownerShare;
    }

    function setRepaymentPeriod(
        uint8 _newRepaymentPeriod
    ) public onlyRole(AUTHORIZED_CONTRACT_ROLE) returns (bool) {
        businessLogicDatas.repaymentPeriod = _newRepaymentPeriod;
        return true;
    }

    function setLendingLimitation(
        uint8 _newLendingLimitationPercent
    ) public onlyRole(AUTHORIZED_CONTRACT_ROLE) returns (bool) {
        businessLogicDatas
            .lendingLimitationPercent = _newLendingLimitationPercent;
        return true;
    }

    function withdrawOwnerShare(
        uint _shareAmount
    ) public onlyOwner(msg.sender) returns (bool) {
        require(
            businessLogicDatas.ownerShare > 0 &&
                _shareAmount <= businessLogicDatas.ownerShare,
            "Insufficient inventory"
        );

        nativeTokenProvider.transfer(msg.sender, _shareAmount);
        businessLogicDatas.ownerShare -= _shareAmount;

        return true;
    }

    function addToken(
        address _tokenAddr,
        address _priceFeedContractAddr
    ) public onlyRole(DATA_MANAGER_ROLE) returns (bool) {
        require(
            _tokenAddr != tokens[_tokenAddr].tokenContractAddr,
            "This token is already added to the contract."
        );

        tokens[_tokenAddr] = Token(
            _tokenAddr,
            IERC20(_tokenAddr),
            AggregatorV3Interface(_priceFeedContractAddr)
        );

        tokensTotalCount++;
        return true;
    }

    function removeToken(
        address _tokenAddr
    )
        public
        onlyRole(DATA_MANAGER_ROLE)
        isTokenSupported(_tokenAddr)
        returns (bool)
    {
        delete tokens[_tokenAddr];
        tokensTotalCount--;

        return true;
    }

    function changeOwner(
        address _newOwnerAddr
    ) public onlyOwner(msg.sender) returns (bool) {
        owner = _newOwnerAddr;
        return true;
    }

    function setBusinessLogicDatas(
        address _networkCoinPriceFeed,
        uint8 _ownerFeePercent,
        uint _voteFee,
        uint8 _lendingLimitationPercent,
        uint _lowestPrice,
        uint _highestPrice,
        uint8 _repaymentPeriod,
        uint _ownerShare
    ) public onlyRole(DATA_MANAGER_ROLE) returns (bool) {
        businessLogicDatas = BusinessLogicDatas(
            _networkCoinPriceFeed,
            _ownerFeePercent,
            _voteFee,
            _lendingLimitationPercent,
            _lowestPrice,
            _highestPrice,
            _repaymentPeriod,
            _ownerShare
        );

        return true;
    }

    function setNativeTokenProvider(
        address _nativeTokenAddr
    ) external onlyRole(DATA_MANAGER_ROLE) {
        require(
            address(nativeTokenProvider) == address(0),
            "The native token currently added !"
        );
        nativeTokenProvider = IERC20(_nativeTokenAddr);
    }

    function calculateBorrowAmount(
        address _tokenAddr,
        uint _collateralAmount
    ) public view returns (uint, uint) {
        uint collateralTotalValue = _collateralAmount.mul(
            getTokenLatestPrice(_tokenAddr)
        );
        uint loanTotalValue = collateralTotalValue
            .mul(businessLogicDatas.lendingLimitationPercent)
            .div(100);
        uint borrowAmount = loanTotalValue.div(estimateBigBangPrice());

        return (borrowAmount, collateralTotalValue);
    }

    function getBorrowAmounts(
        address _tokenAddr,
        uint _collateralAmount
    ) public view isTokenSupported(_tokenAddr) returns (uint, uint, uint) {
        (uint borrowAmount, uint collateralTotalValue) = calculateBorrowAmount(
            _tokenAddr,
            _collateralAmount
        );

        uint borrowAmountOneThousandth = borrowAmount.div(10000);
        uint ownerFeePercent = borrowAmount
            .mul(businessLogicDatas.ownerFeePercent)
            .div(100);
        uint borrowTotalAmount = borrowAmount.sub(ownerFeePercent);

        return (
            borrowAmountOneThousandth,
            borrowTotalAmount,
            collateralTotalValue
        );
    }

    function borrow(
        address _tokenAddr,
        uint _collateralAmount
    ) public payable isTokenSupported(_tokenAddr) returns (bool) {
        require(
            _tokenAddr != address(nativeTokenProvider),
            "You cannot using this token as collateral tokan."
        );
        //   userActivation(msg.sender);

        (
            uint borrowAmountOneThousandth,
            uint borrowTotalAmount,
            uint collateralTotalValue
        ) = getBorrowAmounts(_tokenAddr, _collateralAmount);

        IERC20 token = tokens[_tokenAddr].tokenProvider;

        require(
            borrowTotalAmount <= nativeTokenProvider.balanceOf(address(this)),
            "Insufficient balance in contract !"
        );

        if (_tokenAddr == address(this)) {
            require(msg.value >= _collateralAmount, "Insufficient BNB sent !");
            require(
                msg.value <= address(msg.sender).balance,
                "Insufficient Balance !"
            );

            _collateralAmount = msg.value;
            nativeTokenProvider.transfer(msg.sender, borrowTotalAmount);
        } else {
            require(
                _collateralAmount <= token.balanceOf(msg.sender),
                "Insufficient Balance !"
            );

            token.transferFrom(msg.sender, address(this), _collateralAmount);
            nativeTokenProvider.transfer(msg.sender, borrowTotalAmount);
        }

        Loan memory loan = loans[msg.sender][_tokenAddr];
        loan.borrowedAmount += borrowTotalAmount;
        loan.collateralAmount += _collateralAmount;
        loan.expirationDate = block.timestamp.add(
            businessLogicDatas.repaymentPeriod * 1 days
        );

        loans[msg.sender][_tokenAddr] = loan;
        businessLogicDatas.ownerShare += borrowAmountOneThousandth.mul(
            businessLogicDatas.ownerFeePercent
        );

        usedBigBangsTotalAmount += borrowTotalAmount;
        assetsTotalValue += collateralTotalValue;

        emit Borrowed(address(this), msg.sender, borrowTotalAmount);
        return true;
    }

    function calculateRepaymentAmount(
        address _borrowerAddr,
        address _tokenAddr,
        uint _borrowAmount
    )
        public
        view
        isTokenSupported(_tokenAddr)
        isLoanExist(_borrowerAddr, _tokenAddr)
        returns (uint)
    {
        uint collateralTokenPrice = getTokenLatestPrice(_tokenAddr);
        uint collateralTotalValue = loans[_borrowerAddr][_tokenAddr]
            .collateralAmount
            .mul(collateralTokenPrice);
        uint borrowValue = collateralTotalValue.div(
            loans[_borrowerAddr][_tokenAddr].borrowedAmount
        );
        uint borrowedAmountTotalValue = borrowValue.mul(_borrowAmount);
        uint unlockableCollateral = borrowedAmountTotalValue.div(
            collateralTokenPrice
        );

        return unlockableCollateral;
    }

    function repay(
        address _borrowerAddr,
        address _tokenAddr,
        uint _borrowAmount
    )
        public
        isTokenSupported(_tokenAddr)
        returns (
            //   isLoanExist(_borrowerAddr, _tokenAddr)// Shoulde to be checked by backend logic
            bool
        )
    {
        //   userActivation(msg.sender);

        require(
            _borrowAmount <= loans[_borrowerAddr][_tokenAddr].borrowedAmount,
            "The input amount is greater than the borrowed amount for this loan"
        );
        require(
            _borrowAmount <= nativeTokenProvider.balanceOf(msg.sender),
            "Insufficient Balance !"
        );

        uint unlockedCollateral = calculateRepaymentAmount(
            _borrowerAddr,
            _tokenAddr,
            _borrowAmount
        );

        if (_tokenAddr == address(this)) {
            require(
                getContractBalance(address(this)) > unlockedCollateral,
                "Insufficient BNB balance in contract !"
            );
            payable(msg.sender).transfer(unlockedCollateral);
        } else {
            tokens[_tokenAddr].tokenProvider.transfer(
                msg.sender,
                unlockedCollateral
            );
        }

        nativeTokenProvider.transferFrom(
            msg.sender,
            address(this),
            _borrowAmount
        );

        Loan memory loan = loans[msg.sender][_tokenAddr];
        loan.borrowedAmount -= _borrowAmount;
        loan.collateralAmount -= unlockedCollateral;
        loan.expirationDate = (
            block.timestamp.add(businessLogicDatas.repaymentPeriod)
        );

        loans[msg.sender][_tokenAddr] = loan;

        if (loan.borrowedAmount == 0 ether && loan.collateralAmount == 0 ether)
            delete loans[_borrowerAddr][_tokenAddr];

        usedBigBangsTotalAmount -= _borrowAmount;
        assetsTotalValue -= unlockedCollateral.mul(
            getTokenLatestPrice(_tokenAddr)
        );

        emit Repaid(msg.sender, address(this), _borrowAmount);
        return true;
    }

    function getAssetsTotalValue() public view returns (uint) {
        return assetsTotalValue;
    }

    function getTotalUsedBigBangs() public view returns (uint) {
        return usedBigBangsTotalAmount;
    }

    function estimateBigBangPrice() public view returns (uint) {
        if (getTotalUsedBigBangs() == 0) {
            return (10000000 * 1e10); // it is 0.1 $ now , should to be add one 0 to be 1 $
        } else {
            uint BigbangPrice = getAssetsTotalValue().div(
                getTotalUsedBigBangs()
            );

            if (BigbangPrice < businessLogicDatas.lowestPrice) {
                return businessLogicDatas.lowestPrice;
            } else if (BigbangPrice > businessLogicDatas.highestPrice) {
                return businessLogicDatas.highestPrice;
            } else {
                return BigbangPrice;
            }
        }
    }

    function withdrawSmallAmounts(
        address _tokenAddr
    )
        public
        //   isUserActive(msg.sender)
        isTokenSupported(_tokenAddr)
        isLoanExist(msg.sender, _tokenAddr)
        returns (bool)
    {
        Loan memory loan = loans[msg.sender][_tokenAddr];

        require(
            loan.borrowedAmount == 0 ether &&
                (loan.collateralAmount > 0 ether &&
                    loan.collateralAmount < 1 ether),
            "You cannot execute this transaction !"
        );

        if (_tokenAddr == address(this)) {
            payable(msg.sender).transfer(loan.collateralAmount);
            loan.collateralAmount = 0;
        } else {
            tokens[_tokenAddr].tokenProvider.transfer(
                msg.sender,
                loan.collateralAmount
            );
            loan.collateralAmount = 0;
        }

        if (loan.borrowedAmount == 0 ether && loan.collateralAmount == 0 ether)
            delete loans[msg.sender][_tokenAddr];

        return true;
    }

    receive() external payable {}

    fallback() external payable {}
}

contracts/BigBang_Token.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

// Import necessary OpenZeppelin libraries for ERC20 functionality
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title BigBangToken
 * @dev A comprehensive ERC20 token contract with additional features and access control.
 */
contract BigBangToken is ERC20, ERC20Burnable, AccessControl, Pausable, ERC20Permit{
    address public owner;
    bytes32 public constant ACCESS_CONTROLLER_ROLE = keccak256("ACCESS_CONTROLLER_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant FREEZER_ROLE = keccak256("FREEZER_ROLE");
    bytes32 public constant SNAPSHOT_ROLE = keccak256("SNAPSHOT_ROLE");
    
    mapping(string => bool) private frozenFeatures;

    event FeatureFrozen(string feature, address account);
    event FeatureUnfrozen(string feature, address account);
    event Mint(address indexed to, uint256 indexed amount);
    event Burn(address indexed account, uint256 indexed amount);

    /**
     * @dev Contract constructor
     */
    constructor(address _serviceContractAddr, uint _totalSupply) ERC20("BigBangToken", "BGBT") ERC20Permit("BigBangToken") {
        owner = msg.sender;

        _grantRole(DEFAULT_ADMIN_ROLE, owner);
        _grantRole(ACCESS_CONTROLLER_ROLE, owner);
        _grantRole(MINTER_ROLE, owner);
